CREATE FUNCTION public.calculate_prediction_accuracy(p_model_version character varying DEFAULT NULL::character varying, p_prediction_method character varying DEFAULT NULL::character varying) RETURNS TABLE(model_version character varying, prediction_method character varying, total_predictions bigint, validated_predictions bigint, correct_predictions bigint, accuracy numeric, avg_confidence numeric)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT 
        cpr.model_version,
        cpr.prediction_method,
        COUNT(*) as total_predictions,
        COUNT(cpr.actual_outcome) as validated_predictions,
        SUM(CASE WHEN cpr.predicted_outcome = cpr.actual_outcome THEN 1 ELSE 0 END) as correct_predictions,
        CASE 
            WHEN COUNT(cpr.actual_outcome) > 0 THEN
                ROUND(SUM(CASE WHEN cpr.predicted_outcome = cpr.actual_outcome THEN 1 ELSE 0 END)::DECIMAL / COUNT(cpr.actual_outcome), 3)
            ELSE 0.0
        END as accuracy,
        ROUND(AVG(cpr.prediction_confidence), 3) as avg_confidence
    FROM case_prediction_results cpr
    WHERE (p_model_version IS NULL OR cpr.model_version = p_model_version)
    AND (p_prediction_method IS NULL OR cpr.prediction_method = p_prediction_method)
    GROUP BY cpr.model_version, cpr.prediction_method
    ORDER BY accuracy DESC, avg_confidence DESC;
END;
$$;


