--
-- Name: recalculate_timeline_order(integer); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.recalculate_timeline_order(scenario_id_param integer) RETURNS void
    LANGUAGE plpgsql
    AS $$
DECLARE
    counter INTEGER := 1;
    triple_record RECORD;
BEGIN
    -- Update timeline_order in temporal order
    FOR triple_record IN 
        SELECT id 
        FROM entity_triples
        WHERE scenario_id = scenario_id_param 
        AND temporal_start IS NOT NULL
        ORDER BY temporal_start, id
    LOOP
        UPDATE entity_triples 
        SET timeline_order = counter 
        WHERE id = triple_record.id;
        
        counter := counter + 1;
    END LOOP;
    
    -- For triples without temporal_start, place them at the end
    FOR triple_record IN 
        SELECT id 
        FROM entity_triples
        WHERE scenario_id = scenario_id_param 
        AND temporal_start IS NULL
    LOOP
        UPDATE entity_triples 
        SET timeline_order = counter 
        WHERE id = triple_record.id;
        
        counter := counter + 1;
    END LOOP;
END;
$$;


