--
-- Name: entity_graph; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.entity_graph AS
 SELECT t1.id AS source_id,
    t1.entity_type AS source_type,
    t1.entity_id AS source_entity_id,
    t1.subject AS source_uri,
    t1.predicate AS relationship,
    t2.id AS target_id,
    t2.entity_type AS target_type,
    t2.entity_id AS target_entity_id,
        CASE
            WHEN t2.is_literal THEN (t2.object_literal)::character varying
            ELSE t2.object_uri
        END AS target_uri,
    t1.scenario_id
   FROM (public.entity_triples t1
     JOIN public.entity_triples t2 ON (((t1.object_uri)::text = (t2.subject)::text)))
  WHERE (t1.is_literal = false);


