--
-- Name: prediction_accuracy_summary; Type: VIEW; Schema: public; Owner: postgres
--

CREATE VIEW public.prediction_accuracy_summary AS
 SELECT model_version,
    prediction_method,
    count(*) AS total_predictions,
    count(actual_outcome) AS validated_predictions,
    sum(
        CASE
            WHEN ((predicted_outcome)::text = (actual_outcome)::text) THEN 1
            ELSE 0
        END) AS correct_predictions,
        CASE
            WHEN (count(actual_outcome) > 0) THEN round(((sum(
            CASE
                WHEN ((predicted_outcome)::text = (actual_outcome)::text) THEN 1
                ELSE 0
            END))::numeric / (count(actual_outcome))::numeric), 3)
            ELSE NULL::numeric
        END AS accuracy,
    round(avg(prediction_confidence), 3) AS avg_confidence,
    min(predicted_at) AS first_prediction,
    max(predicted_at) AS latest_prediction
   FROM public.case_prediction_results
  GROUP BY model_version, prediction_method
  ORDER BY
        CASE
            WHEN (count(actual_outcome) > 0) THEN round(((sum(
            CASE
                WHEN ((predicted_outcome)::text = (actual_outcome)::text) THEN 1
                ELSE 0
            END))::numeric / (count(actual_outcome))::numeric), 3)
            ELSE NULL::numeric
        END DESC NULLS LAST, (round(avg(prediction_confidence), 3)) DESC;


