    et.temporal_granularity,
    et.timeline_order,
    et.timeline_group,
    et.temporal_confidence,
    et.temporal_context,
        CASE
            WHEN ((et.entity_type)::text = 'event'::text) THEN e.description
            WHEN ((et.entity_type)::text = 'action'::text) THEN (((a.name)::text || ': '::text) || a.description)
            ELSE NULL::text
        END AS display_name,
        CASE
            WHEN (((et.entity_type)::text = 'action'::text) AND a.is_decision) THEN true
            ELSE false
        END AS is_decision,
        CASE
            WHEN (((et.entity_type)::text = 'action'::text) AND a.is_decision) THEN a.options
            ELSE NULL::json
        END AS decision_options,
        CASE
            WHEN (((et.entity_type)::text = 'action'::text) AND a.is_decision) THEN a.selected_option
            ELSE NULL::character varying
        END AS decision_selected_option
   FROM ((public.entity_triples et
     LEFT JOIN public.events e ON ((((et.entity_type)::text = 'event'::text) AND (et.entity_id = e.id))))
     LEFT JOIN public.actions a ON ((((et.entity_type)::text = 'action'::text) AND (et.entity_id = a.id))))
  ORDER BY et.scenario_id, COALESCE(et.timeline_order, 999999), et.temporal_start;


