CREATE VIEW public.type_mapping_summary AS
 SELECT object_literal AS current_type,
    original_llm_type,
    count(*) AS concept_count,
    avg(type_mapping_confidence) AS avg_confidence,
    sum(
        CASE
            WHEN needs_type_review THEN 1
